document.addEventListener("DOMContentLoaded",function(){(function(){const getAccordion=document.querySelectorAll(".js-toggle-accordion");for(let i=0;i<getAccordion.length;i+=1){getAccordion[i].addEventListener("click",function(e){const getBox=getAccordion[i].querySelector(".accordion-box");if(this.classList.contains("show")===false){this.classList.add("show")}else if(this.classList.contains("show")===true){this.classList.remove("show")}e.stopPropagation()})}})();(function(){const getDropdown=document.querySelectorAll(".js-toggle-dropdown");for(let i=0;i<getDropdown.length;i+=1){getDropdown[i].addEventListener("click",function(e){if(this.classList.contains("show")===false){this.classList.add("show")}else if(this.classList.contains("show")===true){this.classList.remove("show")}e.stopPropagation()})}document.addEventListener("click",function(e){if(e.target!==getDropdown){for(let i=0;i<getDropdown.length;i+=1){getDropdown[i].classList.remove("show")}}})})();(function(){const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i+=1){intLinks[i].addEventListener("click",function(e){e.preventDefault();document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}})();});
