document.addEventListener("DOMContentLoaded",function(){const b=document.querySelectorAll('a[href^="#"]');const d=document.querySelectorAll(".dropdown");const a=d.length;for(var c=0;c<a;c++){d[c].addEventListener("click",function(f){if(!this.classList.contains("drop")){f.stopPropagation();this.classList.add("drop")}else{if(this.classList.contains("drop")){f.stopPropagation();this.classList.remove("drop")}}})}document.addEventListener("click",function(g){if(g.target!=d){for(var f=0;f<a;f++){d[f].classList.remove("drop")}}});for(var c=0;c<b.length;c++){b[c].addEventListener("click",function(f){f.preventDefault();document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}});
