$(function(){$(".dropdown").each(function(){$(this).on("click",function(){$(this).children(".menu").slideToggle(500)})});$(window).on("scroll",function(){var a=$(".menu");a.slideUp(500)});$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var a=$(this.hash);a=a.length?a:$("[name="+this.hash.slice(1)+"]");if(a.length){$("html, body").animate({scrollTop:a.offset().top},1000);return false}}})});