document.addEventListener("DOMContentLoaded",function(){const b=document.querySelectorAll(".dropdown");const c=b.length;for(var a=0;a<c;a++){b[a].addEventListener("click",function(d){if(!this.classList.contains("drop")){d.stopPropagation();this.classList.add("drop")}else{if(this.classList.contains("drop")){d.stopPropagation();this.classList.remove("drop")}}})}document.addEventListener("click",function(f){if(f.target!=b){for(var d=0;d<c;d++){b[d].classList.remove("drop")}}})});