document.addEventListener("DOMContentLoaded",function(){(function(){const getAccordion=document.querySelectorAll(".js-toggle-accordion");for(let i=0;i<getAccordion.length;i+=1){getAccordion[i].addEventListener("click",function(e){e.stopPropagation();if(this.classList.contains("show")===false){this.classList.add("show");console.log("Add class show");return}if(this.classList.contains("show")===true){const thisChildren=this.children;for(let i=0;i<thisChildren.length;i+=1){if(thisChildren[i].classList.contains("accordion-box")===true){console.log("True")}}this.classList.remove("show");console.log("Remove class show")}})}})();(function(){const getDropdown=document.querySelectorAll(".js-toggle-dropdown");for(let i=0;i<getDropdown.length;i+=1){getDropdown[i].addEventListener("click",function(e){e.stopPropagation();if(this.classList.contains("show")===false){this.classList.add("show");return}if(this.classList.contains("show")===true){this.classList.remove("show")}})}document.addEventListener("click",function(e){if(e.target!==getDropdown){for(let i=0;i<getDropdown.length;i+=1){getDropdown[i].classList.remove("show")}}})})();(function(){const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i+=1){intLinks[i].addEventListener("click",function(e){e.preventDefault();document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}})();});
