document.addEventListener("DOMContentLoaded",function(){for(var d=document.querySelectorAll("a[href^='#']"),c=document.querySelectorAll(".dropdown"),e=c.length,b=0;b<e;b++)c[b].addEventListener("click",function(a){this.classList.contains("drop")?this.classList.contains("drop")&&(a.stopPropagation(),this.classList.remove("drop")):(a.stopPropagation(),this.classList.add("drop"))});document.addEventListener("click",function(a){if(a.target!=c)for(a=0;a<e;a++)c[a].classList.remove("drop")});for(b=0;b<
d.length;b++)d[b].addEventListener("click",function(a){a.preventDefault();document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});
