document.addEventListener("DOMContentLoaded",function(){let a;const b=document.querySelectorAll(".dropdown"),c=b.length;for(a=0;a<c;a++)b[a].addEventListener("click",function(a){a.stopPropagation(),this.classList.contains("drop")?this.classList.remove("drop"):this.classList.add("drop")});document.addEventListener("click",function(d){if(d.target!=b)for(a=0;a<c;a++)b[a].classList.remove("drop")});const d=document.querySelectorAll("a[href^='#']");for(a=0;a<d.length;a++)d[a].addEventListener("click",function(a){a.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});
