document.addEventListener("DOMContentLoaded",function(){const b=$(".dropdown");const a=$(".menu");b.each(function(){$(this).on("click",function(){$(this).children(".menu").slideToggle(500)})});$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var c=$(this.hash);c=c.length?c:$("[name="+this.hash.slice(1)+"]");a.slideUp(500);if(c.length){$("html, body").animate({scrollTop:c.offset().top},1000);return false}}})});