document.addEventListener("DOMContentLoaded",function(){const c=document.querySelectorAll(".dropdown");const a=c.length;for(var b=0;b<a;b++){c[b].addEventListener("click",function(d){if(!this.classList.contains("drop")){d.stopPropagation();this.classList.add("drop")}else{if(this.classList.contains("drop")){d.stopPropagation();this.classList.remove("drop")}}})}document.addEventListener("click",function(f){if(f.target!=c){for(var d=0;d<a;d++){c[d].classList.remove("drop")}}})});
